import { Component, Inject } from '@angular/core';
import { HttpClient } from '@angular/common/http';
import * as _ from 'lodash';

@Component({
  selector: 'app-fetch-data',
  templateUrl: './fetch-data.component.html',
  styleUrls: ['./fetch-data.component.less']
})
export class FetchDataComponent {

  public bots: IBot[] = [];
  private _bots: IBot[] = [];
  public filters: IFilter[] = [];

  constructor(http: HttpClient, @Inject('BASE_URL') baseUrl: string) {
    http.get<IBots>('http://localhost:7234/api/Bots').subscribe(result => {
      this._bots = result.items;
      this.bots = result.items;
    }, error => console.error(error));
  }

  public clearFilter() {
    this.bots = this._bots;
    this.filters = [];
  }
  public clickBotId = () => {
    console.log('clickbotid');
  }
  public clickBotProperty = (property: string, value: string) => {
    this.filters.push({ property, value })

    this._filter();

    console.log('clickBotProperty');
    console.log(`${property} : ${value}`);
  }
  private _filter = () => {
    this.bots = this._bots.filter((v, i, a) => {
      let include = true;
      this.filters.forEach(x => {
        if (v[x.property as keyof IBot] !== x.value) include = false;
      });
      return include;
    });
  }
  public clickFilterProperty = (property: string, value: string) => {
    this.filters = this.filters.filter((v, i, a) => {
      if (v.property === property && v.value === value) return false;
      return true;
    });

    this._filter();

    console.log('clickFilterProperty');
    console.log(`${property} : ${value}`);
  }
}

interface IBots {
  items: IBot[]
}
interface IBot {
  id: string,
  business: string,
  city: string,
  region: string,
  country: string,
  lat: number,
  lon: number,
  dateModified: Date,
  dateCreated: Date,
}
interface IFilter {
  property: string,
  value: string
}
