import { Component, Inject } from '@angular/core';
import { HttpClient } from '@angular/common/http';
import * as _ from 'lodash';

@Component({
  selector: 'app-fetch-data',
  templateUrl: './fetch-data.component.html',
  styleUrls: ['./fetch-data.component.less']
})

export class FetchDataComponent {

  public bots: IBot[] = [];
  private _bots: IBot[] = [];
  public filters: IFilter[] = [];

  public countries: string[] = [];
  public regions: string[] = [];
  public cities: string[] = [];
  public businesses: string[] = [];

  constructor(http: HttpClient, @Inject('BASE_URL') baseUrl: string) {
    http.get<IBots>('http://localhost:7234/api/Bots').subscribe(result => {
      //set master list
      this._bots = result.items;
      //set working list
      this.bots = result.items;
      this._buildFilterableItems();

    }, error => console.error(error));
  }

  //- clears the filters and resets the bot list to the master list
  public clearFilter() {
    this.bots = this._bots;
    this.filters = [];
    this._buildFilterableItems();
  }
  public clickBotId = () => {
    console.log('clickbotid');
  }
  //- adds a new filter if it isn't a duplicate
  public clickBotProperty = (property: string, value: string) => {
    if (!this.filters.some(f => f.property === property && f.value === value)) {
      this.filters.push({ property, value })

      this._filter();
    }
  }
  //- removes an existing filter
  public clickFilterProperty = (property: string, value: string) => {
    this.filters = this.filters.filter((v, i, a) => {
      if (v.property === property && v.value === value) return false;
      return true;
    });

    this._filter();
  }

  private _filter = () => {
    this.bots = this._bots.filter((v, i, a) => {
      let include = true;
      this.filters.forEach(x => {
        if (v[x.property as keyof IBot] !== x.value) include = false;
      });
      return include;
    });
    this._buildFilterableItems();
  }
  private _buildFilterableItems = () => {
    this.countries = [...new Set(this.bots.map((v) => v.country).sort())];
    this.regions = [...new Set(this.bots.map((v) => v.region).sort())];
    this.cities = [...new Set(this.bots.map((v) => v.city).sort())];
    this.businesses = [...new Set(this.bots.map((v) => v.business).sort())];
  }
}

interface IBots {
  items: IBot[]
}
interface IBot {
  id: string,
  business: string,
  city: string,
  region: string,
  country: string,
  lat: number,
  lon: number,
  dateModified: Date,
  dateCreated: Date,
}
interface IFilter {
  property: string,
  value: string
}
