import { Component } from '@angular/core';

@Component({
  selector: 'app-root',
  templateUrl: './app.component.html'
})
export class AppComponent {
  title = 'app';

  errors: number = 0;

  constructor() { }

  public receiveMessage = ($event: any) => {
    console.log('received');
    this.errors = $event;
  }
}
